import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Div from '../Div';
import './servicelist.scss';
const serviceData = [
  {
    title: 'Partnerships',
    subtitle:
      'Unlock lucrative partnerships and strategic alliances with our expert guidance. Gain access to our extensive network of global brands in the entertainment space, ensuring unparalleled opportunities for collaboration. From identifying the perfect collaborators to managing the entire negotiation process, we ensure seamless partnerships that drive results.',
    imgUrl: '/images/service_7.jpeg',
    href: '/service/service-details',
  },
  {
    title: 'Management',
    subtitle:
      'Elevate your influence with our personalised management services. Were more than just managers — were your dedicated partners in success. With a genuine commitment to your growth and well-being, well nurture your online presence, optimise brand collaborations, and support you every step of the way as you reach new heights in the digital realm.',
    imgUrl: 'https://www.shutterstock.com/image-photo/mature-businessman-leader-mentor-talking-600nw-2024994161.jpg',
    href: '/service/service-details',
  },
  {
    title: 'Content Creation',
    subtitle:
      'Backed by a seasoned team of professionals boasting over a decade of experience and 10 billion views gained, we offer unparalleled expertise to enhance your digital presence. Through strategic content planning and innovative ideation sessions, well collaborate with you to craft compelling stories that resonate with your audience and drive impactful engagement.',
    imgUrl: 'https://www.shutterstock.com/image-photo/vertical-medium-closeup-studio-portrait-600nw-2098512709.jpg',
    href: '/service/service-details',
  },
  {
    title: 'Social Media Management',
    subtitle:
      'With a proven track record of over 10 years of experience and 100 billion impressions gained, our seasoned team is equipped to guide you through every aspect of your social media journey. From crafting tailored strategies to creative content creation and beyond, well  ensure your brand stands out in the digital landscape.',
    imgUrl: '/images/service_10.jpeg',
    href: '/service/service-details',
  },
];

export default function ServiceList({ variant }) {
  const [active, setActive] = useState(0);
  const handelActive = index => {
    setActive(index);
  };
  return (
    <Div className={`cs-iconbox_3_list ${variant ? variant : ''}`}>
      {serviceData.map((item, index) => (
        <Div
          className={`cs-hover_tab ${active === index ? 'active' : ''}`}
          key={index}
          onMouseEnter={() => handelActive(index)}
        >
          <Link to={item.href} className="cs-iconbox cs-style3">
            <>
              <Div className="cs-image_layer cs-style1 cs-size_md">
                <Div className="cs-image_layer_in">
                  <img
                    src={item.imgUrl}
                    alt="Thumb"
                    className="w-100 cs-radius_15"
                  />
                </Div>
              </Div>
              <span className="cs-iconbox_icon cs-center">
                <svg
                  width={30}
                  height={29}
                  viewBox="0 0 30 29"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M29.3803 3.05172C29.4089 1.94752 28.537 1.02921 27.4328 1.00062L9.43879 0.534749C8.33459 0.506159 7.41628 1.37811 7.38769 2.48231C7.35911 3.58651 8.23106 4.50482 9.33526 4.53341L25.3299 4.94752L24.9158 20.9422C24.8872 22.0464 25.7592 22.9647 26.8634 22.9933C27.9676 23.0218 28.8859 22.1499 28.9144 21.0457L29.3803 3.05172ZM3.37714 28.5502L28.7581 4.4503L26.0039 1.54961L0.622863 25.6495L3.37714 28.5502Z"
                    fill="currentColor"
                  />
                </svg>
              </span>
              <Div className="cs-iconbox_in">
                <h2 className="cs-iconbox_title">{item.title}</h2>
                <Div className="cs-iconbox_subtitle">{item.subtitle}</Div>
              </Div>
            </>
          </Link>
        </Div>
      ))}
    </Div>
  );
}
