import React, { useEffect, useState } from 'react';
import { Icon } from '@iconify/react';
import { pageTitle } from '../../helper';
import Div from '../Div';
import PageHeading from '../PageHeading';
import SectionHeading from '../SectionHeading';
import Spacing from '../Spacing';
import ContactInfoWidget from '../Widget/ContactInfoWidget';
import { useForm } from "react-hook-form";
import useWeb3Forms from "@web3forms/react";

function Contact() {
  const {
    register,
    handleSubmit,
    reset,
    watch,
    control,
    setValue,
    formState: { errors, isSubmitSuccessful, isSubmitting },
  } = useForm({
    mode: "onTouched",
  });
  const [isSuccess, setIsSuccess] = useState(false);
  const [message, setMessage] = useState(false);

  // Please update the Access Key in the .env
  const apiKey = "YOUR_ACCESS_KEY_HERE";
  const { submit: onSubmit } = useWeb3Forms({
    access_key: apiKey,
    settings: {
      from_name: "Acme Inc",
      subject: "New Contact Message from your Website",
    },
    onSuccess: (msg, data) => {
      setIsSuccess(true);
      setMessage(msg);
      reset();
    },
    onError: (msg, data) => {
      setIsSuccess(false);
      setMessage(msg);
    },
  });
}

export default function ContactPage() {
  const [formType, setFormType] = useState('creator'); // Initialize formType state with 'creator'

  // Set the page title and scroll to top on component mount
  useEffect(() => {
    pageTitle('Contact Us');
    window.scrollTo(0, 0);
  }, []);

  // Function to handle form type selection
  const handleFormType = (type) => {
    setFormType(type);
  };


  // Function to render different forms based on the selected type
  const renderForm = () => {
    switch (formType) {
      case 'brand':
        return (
          <>
            {/* Creator form */}
            <Div className="col-sm-12">
              <label className="cs-primary_color">Budget</label>
              <input type="text" className="cs-form_field" />
              <Spacing lg="20" md="20" />
            </Div>
            <Div className="col-sm-12">
              <label className="cs-primary_color">Message</label>
              <textarea
                className="cs-form_field"
                style={{ height: '120px' }} // Adjust height as needed
                placeholder="Enter your message here..."
                readOnly
              />
              <Spacing lg="20" md="20" />
            </Div>
            {/* Additional fields for creator */}
          </>
        );
      case 'creator':
        return (
          <>
            {/* Brand form */}
            <Div className="col-sm-12">
              <label className="cs-primary_color">Links</label>
              <input
                type="text"
                className="cs-form_field"
                placeholder="Youtube"
                readOnly
              />
              <Spacing lg="20" md="20" />
            </Div>
            <Div className="col-sm-12">
              <input
                type="text"
                className="cs-form_field"
                placeholder="Instagram"
                readOnly
              />
              <Spacing lg="20" md="20" />
            </Div>
            <Div className="col-sm-12">
              <input
                type="text"
                className="cs-form_field"
                placeholder="tiktok"
                readOnly
              />
              <Spacing lg="20" md="20" />
            </Div>
            <Div className="col-sm-12">
              <input
                type="text"
                className="cs-form_field"
                placeholder="twitter"
                readOnly
              />
              <Spacing lg="20" md="20" />
            </Div>
            <Div className="col-sm-12">
              <input
                type="text"
                className="cs-form_field"
                placeholder="twitch"
                readOnly
              />
              <Spacing lg="20" md="20" />
            </Div>
            <Div className="col-sm-12">
