import React, { useEffect, useState } from 'react';
import { Icon } from '@iconify/react';
import { pageTitle } from '../../helper';
import Div from '../Div';
import PageHeading from '../PageHeading';
import SectionHeading from '../SectionHeading';
import Spacing from '../Spacing';
import ContactInfoWidget from '../Widget/ContactInfoWidget';
import { useForm, useWatch } from 'react-hook-form'; // Fix import statement for useForm and useWatch
import useWeb3Forms from '@web3forms/react';

function ContactForm() {
  const {
    register,
    handleSubmit,
    setValue,
    reset,
    control,
    formState: { errors, isSubmitSuccessful, isSubmitting },
  } = useForm({
    mode: 'onTouched',
  });
  const [isSuccess, setIsSuccess] = React.useState(false);
  const [Message, setMessage] = React.useState('');

  const userName = useWatch({
    control,
    name: 'name',
    defaultValue: 'Someone',
  });

  useEffect(() => {
    setValue('subject', `${userName} sent a message from Website`);
  }, [userName, setValue]);

  const onSubmit = async (data, e) => {
    console.log(data);
    await fetch('https://api.web3forms.com/submit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
      },
      body: JSON.stringify(data, null, 2),
    })
      .then(async (response) => {
        let json = await response.json();
        if (json.success) {
          setIsSuccess(true);
          setMessage(json.message);
          e.target.reset();
          reset();
        } else {
          setIsSuccess(false);
          setMessage(json.message);
        }
      })
      .catch((error) => {
        setIsSuccess(false);
        setMessage('Client Error. Please check the console.log for more info');
        console.log(error);
      });
  };
}

export default function ContactPage() {
  const [formType, setFormType] = useState('creator'); // Initialize formType state with 'creator'

  // Set the page title and scroll to top on component mount
  useEffect(() => {
    pageTitle('Contact Us');
    window.scrollTo(0, 0);
  }, []);

  // Function to handle form type selection
  const handleFormType = (type) => {
    setFormType(type);
  };

  // Function to render different forms based on the selected type
  const renderForm = () => {
    switch (formType) {
      case 'brand':
        return (
          <>
            {/* Creator form */}
            <Div className="col-sm-12">
              <label className="cs-primary_color">Budget</label>
              <input type="text" className="cs-form_field" />
              <Spacing lg="20" md="20" />
            </Div>
            <Div className="col-sm-12">
              <label className="cs-primary_color">Message</label>
              <textarea
                className="cs-form_field"
                style={{ height: '120px' }} // Adjust height as needed
                placeholder="Enter your message here..."
                readOnly
              />
              <Spacing lg="20" md="20" />
            </Div>
            {/* Additional fields for creator */}
          </>
        );
      case 'creator':
        return (
          <>
            {/* Brand form */}
            <Div className="col-sm-12">
              <label className="cs-primary_color">Links</label>
              <input
                type="text"
                className="cs-form_field"
                placeholder="Youtube"
