import React, { useEffect, useState } from 'react';
import { Icon } from '@iconify/react';
import { useForm } from 'react-hook-form'; // Import react-hook-form library
import useWeb3Forms from "@web3forms/react";
import { pageTitle } from '../../helper';
import Div from '../Div';
import PageHeading from '../PageHeading';
import SectionHeading from '../SectionHeading';
import Spacing from '../Spacing';
import ContactInfoWidget from '../Widget/ContactInfoWidget';

function ContactPage() {
  const {
    register,
    handleSubmit,
    reset,
    watch,
    control,
    setValue,
    formState: { errors, isSubmitSuccessful, isSubmitting },
  } = useForm({
    mode: "onTouched",
  });
  const [isSuccess, setIsSuccess] = useState(false);
  const [message, setMessage] = useState(false);

  // Please update the Access Key in the .env
  const apiKey = process.env.PUBLIC_ACCESS_KEY || "4fffa478-d53d-44ad-8b04-e47740bb8194";

  const { submit: onSubmit } = useWeb3Forms({
    access_key: apiKey,
    settings: {
      from_name: "Acme Inc",
      subject: "New Contact Message from your Website",
    },
    onSuccess: (msg, data) => {
      setIsSuccess(true);
      setMessage(msg);
      reset();
    },
    onError: (msg, data) => {
      setIsSuccess(false);
      setMessage(msg);
    },
  });

  // Function to handle form submission
  const handleFormSubmit = (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    // Call your form submission function here (I'm assuming it's onSubmit from useWeb3Forms)
    onSubmit(event.target);
  };

  // Other parts of your component...

  return (
    <>
      {/* Your component JSX */}
      <form onSubmit={handleFormSubmit} className="row">
        {/* Form fields */}
        <Div className="col-sm-12">
          <label className="cs-primary_color">Full Name*</label>
          <input type="text" className="cs-form_field" />
          <Spacing lg="20" md="20" />
        </Div>
        <Div className="col-sm-12">
          <label className="cs-primary_color">Email*</label>
          <input type="text" className="cs-form_field" />
          <Spacing lg="20" md="20" />
        </Div>
        <Div className="col-sm-12">
          <label className="cs-primary_color">Company</label>
          <input type="text" className="cs-form_field" />
          <Spacing lg="20" md="20" />
        </Div>
        <Div className="col-sm-12">
          <label className="cs-primary_color">Where did you find us?</label>
          <input type="text" className="cs-form_field" />
          <Spacing lg="20" md="20" />
        </Div>
        {/* Render additional fields based on form type */}
        <Div className="col-sm-12">
          <button type="submit" className="cs-btn"> {/* Change button type to submit */}
            <span>Send Message</span>
            <Icon icon="bi:arrow-right" />
          </button>
        </Div>
      </form>
      {/* Your component JSX */}
    </>
  );
}

export default ContactPage;
