import React, { useEffect, useState } from 'react';
import { Icon } from '@iconify/react';
import { pageTitle } from '../../helper';
import Div from '../Div';
import PageHeading from '../PageHeading';
import SectionHeading from '../SectionHeading';
import Spacing from '../Spacing';
import ContactInfoWidget from '../Widget/ContactInfoWidget';
import useWeb3Forms from "@web3forms/react";
import { useForm } from "react-hook-form"; // Add this import

// Move the Contact function outside of the ContactPage component
function Contact() {
  const {
    register,
    handleSubmit,
    reset,
    watch,
    control,
    setValue,
    formState: { errors, isSubmitSuccessful, isSubmitting },
  } = useForm({
    mode: "onTouched",
  });
  const [isSuccess, setIsSuccess] = useState(false);
  const [message, setMessage] = useState(false);

  // Please update the Access Key in the .env
  const apiKey = process.env.PUBLIC_ACCESS_KEY || "4fffa478-d53d-44ad-8b04-e47740bb8194";

  const { submit: onSubmit } = useWeb3Forms({
    access_key: apiKey,
    settings: {
      from_name: "Acme Inc",
      subject: "New Contact Message from your Website",
    },
    onSuccess: (msg, data) => {
      setIsSuccess(true);
      setMessage(msg);
      reset();
    },
    onError: (msg, data) => {
      setIsSuccess(false);
      setMessage(msg);
    },
  });

  return (
    <>
      <form onSubmit={handleSubmit(onSubmit)} className="row">
        <Div className="col-sm-6">
          <label className="cs-primary_color">Full Name*</label>
          <input type="text" className="cs-form_field" {...register("name", { required: true })} />
          {errors.name && <span className="text-red-500">Full name is required</span>}
          <Spacing lg="20" md="20" />
        </Div>
        <Div className="col-sm-6">
          <label className="cs-primary_color">Email*</label>
          <input type="text" className="cs-form_field" {...register("email", { required: true })} />
          {errors.email && <span className="text-red-500">Email is required</span>}
          <Spacing lg="20" md="20" />
        </Div>
        <Div className="col-sm-6">
          <label className="cs-primary_color">Company</label>
          <input type="text" className="cs-form_field" {...register("company")} />
          <Spacing lg="20" md="20" />
        </Div>
        <Div className="col-sm-6">
          <label className="cs-primary_color">Where did you find us?</label>
          <input type="text" className="cs-form_field" {...register("source")} />
          <Spacing lg="20" md="20" />
        </Div>
        {/* Additional fields for both creator and brand */}
        <Div className="col-sm-12">
          <label className="cs-primary_color">Message</label>
          <textarea
            className="cs-form_field"
            style={{ height: '120px' }}
            placeholder="Enter your message here..."
            {...register("message")}
          />
          <Spacing lg="20" md="20" />
        </Div>
        <Div className="col-sm-12">
          <button className="cs-btn">
            <span>Send Message</span>
            <Icon icon="bi:arrow-right" />
          </button>
        </Div>
      </form>
    </>
  );
}

export default function ContactPage() {
  const [formType, setFormType] = useState('creator'); // Initialize formType state with 'creator'

  // Set the page title and scroll to top on component mount
  useEffect(() => {
    pageTitle('Contact Us');
    window.scrollTo(0, 0);
  }, []);

  // Function to handle form type selection
  const handleFormType = (type) => {
    setFormType(type);
  };

  // Function to render different forms based on the selected type
  const renderForm = () => {
    switch (formType) {
      case 'brand':
        return (
          <>
            {/* Creator form */}
            {/* Additional fields for creator */}
          </>
        );
      case 'creator':
        return (
          <>
            {/* Brand form */}
            {/* Additional fields for brand */}
          </>
        );
      default:
        return null;
    }
  };

  return (
    <>
      <style>
        {`
          .cs-btn {
            border: 2px solid #FF4A17; /* Orange outline */
            background-color: black; /* Black background */
            color: white; /* White text */
            border-radius: 10px; /* Rounded corners */
            padding: 10px 20px; /* Adjust padding as needed */
            margin-right: 10px; /* Adjust margin as needed */
            cursor: pointer; /* Cursor style */
            transition: background-color 0.3s, color 0.3s; /* Smooth transition */
          }

          .cs-btn:hover {
            background-color: #FF4A17; /* Orange background on hover */
            color: black; /* Black text on hover */
          }
        `}
      </style>

      <Spacing lg="150" md="80" />
      <Div className="container">
        <Div className="row">
          <Div className="col-lg-6">
            <SectionHeading
              title="Do you have a project <br/>in your mind?"
              subtitle="Getting Touch"
            />
            <Spacing lg="55" md="30" />
            <ContactInfoWidget withIcon />
            <Spacing lg="0" md="50" />
          </Div>
          <Div className="col-lg-6">
            {/* Form selection buttons */}
            <div>
              <button
                className="cs-btn" // Apply custom button style
                onClick={() => handleFormType('creator')}
              >
                Creator
              </button>
              <button
                className="cs-btn" // Apply custom button style
                onClick={() => handleFormType('brand')}
              >
                Brand
              </button>
            </div>
            {/* Add some space */}
            <Spacing lg="20" md="10" />
            {/* Main contact form */}
            {renderForm()}
            {/* Add some space after the form */}
            <Spacing lg="20" md="10" />
          </Div>
        </Div>
      </Div>
      <Spacing lg="150" md="80" />
      {/* Google Map */}
      <Div className="cs-google_map">
        <iframe
          src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d96652.27317354927!2d-74.33557928194516!3d40.79756494697628!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89c3a82f1352d0dd%3A0x81d4f72c4435aab5!2sTroy+Meadows+Wetlands!5e0!3m2!1sen!2sbd!4v1563075599994!5m2!1sen!2sbd"
          allowFullScreen
          title="Google Map"
        />
      </Div>
      <Spacing lg="50" md="40" />
    </>
  );
}
